INSERT INTO `blog_article` VALUES (2025, '图片上传测试', '图片上传测试', '<p><br></p><h3>图片上传测试<br></h3><p><img style=\"width: 1523px;\" src=\"/temp/images/5f768c08-a7f3-4c85-88a4-d260ee194a7c.jpg\"></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着internet的普及和电子商务的兴起，网上图书销售是Internet电子商务在图书销售行业发展的必然结果，这种新型的图书销售形式，与传统利用书店进行销售的方式相比拥有许多优势：一是降低了销售成本；二是利用网络作为交易平台，改变传统的交易方式，使得交易活动不受空间和时间的限制；三是信息的传递更迅速灵活，新书信息上传后，客户可以立即看到，交易马上可以从网上进行，从而大大提高了交易的效率。正是由于这些优势，网上图书销售才能得以迅速地发展。网上图书销售系统的主要功能是利用网站作为交易平台，将图书的一些基本信息以网站的形式发布到Internet中，客户可以通过Internet登录图书销售网站来查看售书信息并提交订单订购图书，实现在线交易。</p><pre>/**<br>&nbsp;* 保存文章<br>&nbsp;* @param status<br>&nbsp;*/<br>function subArticle(status) {<br>&nbsp;&nbsp;&nbsp; var content = $(\'#fmtType\').val() == \'markdown\' ? mditor.value : htmlEditor.summernote(\'code\');<br>&nbsp;&nbsp;&nbsp; var title = $(\'#articleForm input[name=title]\').val();<br>&nbsp;&nbsp;&nbsp; if (title == \'\') {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tale.alertWarn(\'请输入文章标题\');<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return;<br>&nbsp;&nbsp;&nbsp; }<br>&nbsp;&nbsp;&nbsp; if (content == \'\') {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tale.alertWarn(\'请输入文章内容\');<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return;<br>&nbsp;&nbsp;&nbsp; }<br>&nbsp;&nbsp;&nbsp; clearInterval(refreshIntervalId);<br>&nbsp;&nbsp;&nbsp; $(\'#content-editor\').val(content);<br>&nbsp;&nbsp;&nbsp; $(\"#articleForm #status\").val(status);<br>&nbsp;&nbsp;&nbsp; $(\"#articleForm #categories\").val($(\'#multiple-sel\').val());<br>&nbsp;&nbsp;&nbsp; var params = $(\"#articleForm\").serialize();<br>&nbsp;&nbsp;&nbsp; var url = $(\'#articleForm #cid\').val() != \'\' ? \'/admin/article/modify\' : \'/admin/article/publish\';<br>&nbsp;&nbsp;&nbsp; tale.post({<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; url: url,<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; data: params,<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; success: function (result) {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (result &amp;&amp; result.success) {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tale.alertOk({<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; text: \'文章保存成功\',<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; then: function () {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; setTimeout(function () {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; window.location.href = \'/admin/article\';<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }, 500);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tale.alertError(result.msg || \'保存文章失败\');<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br>&nbsp;&nbsp;&nbsp; });<br>}<br></pre><p><br></p><p><br></p>', '1', '2018-09-03 21:15:26', '2018-09-06 21:34:58', 22, 'true', '小说', 1, 0, 'true', NULL);
